// Task Router Classification Logic
// Classifies incoming tasks into T1/T2/T3 tiers

const task = $input.first().json;

// Initialize complexity score
let score = 0;

// Signal 1: File count
const fileCount = task.files?.length || 1;
if (fileCount <= 2) {
  score += 1;
} else if (fileCount <= 5) {
  score += 2;
} else {
  score += 3;
}

// Signal 2: Risk indicators in description
const description = (task.description || '').toLowerCase();
if (description.includes('security')) score += 1;
if (description.includes('architecture')) score += 1;
if (description.includes('refactor')) score += 1;
if (description.includes('migration')) score += 2;
if (description.includes('database')) score += 1;
if (description.includes('api')) score += 1;

// Signal 3: Test coverage status
if (task.has_tests === false) score += 1;

// Signal 4: Priority indicator
if (task.priority === 'critical') score += 1;
if (task.priority === 'high') score += 1;

// Determine tier based on score
let tier, label, tool, color;
if (score <= 2) {
  tier = 'T1';
  label = 'routine';
  tool = 'Copilot';
  color = 'green';
} else if (score <= 4) {
  tier = 'T2';
  label = 'analytical';
  tool = 'Cursor/ChatGPT';
  color = 'yellow';
} else {
  tier = 'T3';
  label = 'complex';
  tool = 'Claude Code';
  color = 'red';
}

return {
  json: {
    ...task,
    classification: {
      tier,
      label,
      tool,
      score,
      color
    },
    github_label: tier.toLowerCase(),
    classified_at: new Date().toISOString()
  }
};

